package com.ly.neuter.core.utils;import android.app.Dialog;import android.view.View;import android.view.ViewGroup;import android.widget.DatePicker;/** * 日期选择对话框工具 * */public class DatePickerUtil {    /**     * 设置设置项状态     *     * @param dialog     * @param index  0:年 1:月 2:日     * @param status false:View.GONE, true:View.VISIBLE     * @return     */    public static Dialog setDatePickerStatus(Dialog dialog, int index, boolean status) {        DatePicker dp = findDatePicker((ViewGroup) dialog.getWindow().getDecorView());        if (dp != null) {            ((ViewGroup) ((ViewGroup) dp.getChildAt(0)).getChildAt(0)).getChildAt(index)                    .setVisibility(status ? View.VISIBLE : View.GONE);        }        return dialog;    }    /**     * 从当前Dialog中查找DatePicker子控件     *     * @param group     * @return     */    private static DatePicker findDatePicker(ViewGroup group) {        if (group != null) {            for (int i = 0, j = group.getChildCount(); i < j; i++) {                View child = group.getChildAt(i);                if (child instanceof DatePicker) {                    return (DatePicker) child;                } else if (child instanceof ViewGroup) {                    DatePicker result = findDatePicker((ViewGroup) child);                    if (result != null)                        return result;                }            }        }        return null;    }}